YUI.add('srpl-hero-templates', function(Y){
// DO NOT EDIT THIS FILE
// this should be automatically generated by using the .*.html files in templates
Y.namespace('srpl.Hero');
Y.srpl.Hero.templates = {
    actions:Y.template('<ul class="srpl-actions clearfix">\n    <li class="first-child">\n        <button style="background-color:<%= h.get(\'palette\').button %>;color:<%= h.get(\'palette\').text %>" title="Send to phone" type="button" class="send-phone transparent">\n            <i class="icon"></i>\n            <div class="text f-right">Send to phone</div>\n       </button>\n    </li>\n    <li>\n        <button style="background-color:<%= h.get(\'palette\').button %>;color:<%= h.get(\'palette\').text %>" title="Directions" type="button" class="srpl-directions transparent">\n            <i class="icon"></i>\n            <div class="text f-right">Directions</div>\n       </button>\n    </li>\n    <li>\n        <button style="background-color:<%= h.get(\'palette\').button %>;color:<%= h.get(\'palette\').text %>" title="Share" type="button" class="share transparent">\n            <i class="icon"></i>\n            <div class="text f-right">Share</div>\n       </button>\n    </li>\n    <li class="last-child">\n        <button style="background-color:<%= h.get(\'palette\').button %>;color:<%= h.get(\'palette\').text %>" title="Menu" type="button" class="menu transparent">\n            <i class="icon"></i>\n            <div class="text f-right">Menu</div>\n       </button>\n    </li>\n</ul>'),
		base:Y.template('<div class="srcpl-hero-container" style="color:<%= h.get(\'palette\').text %>">\n    <img class="hero" src="<%= h.get(\'image\').src %>" alt="<%= b.get(\'title\') %>" height="<%= h.get(\'image\').height %>" width="<%= h.get(\'image\').width %>" />\n    <div class="srcpl-hero-content-container">\n        <h1><%= b.get(\'title\') %></h1>\n        <h2><%= b.get(\'primary_category\') %></h2>\n        <% if (r.get(\'averageRating\') && !isNaN(r.get(\'averageRating\'))) { %>\n        <p class="srpl-rating-review">\n            <a target="_blank" href="<%= r.get(\'reviewUrl\') %>">\n                <span class="srpl-rating-static f-left srpl-rating-<%= (r.get(\'averageRating\')*10) %>">\n                    <%= r.get(\'averageRating\') %> out of 5 stars\n                </span>\n                <span class="srpl-review"><%= r.get(\'reviewCount\') %> reviews on <%= r.get(\'source\') %></span>\n            </a>\n        </p>\n        <% } if (b.get(\'address\')) {%>\n        <p class="adr"><%= b.get(\'address\') %></p>\n        <% } if (b.get(\'phone\')) {%>\n        <p class="tel"><%= b.get(\'phone\') %></p>\n        <% } if (b.get(\'website\') && b.get(\'website\').url) {%>\n        <p class="web">\n            <a href="<%= b.get(\'website\').url %>" target="_blank" class="url" title="<%= b.get(\'website\').label %>">\n                <%= b.get(\'website\').label %>\n            </a>\n        </p>\n        <% } %>\n        <%= Y.srpl.Hero.templates.actions({h:h}) %>\n    </div>\n</div>')
};
}, '@VERSION@',{
    // [bug 5892107] no need to require assets     
    requires:['srpl-template']
});